@media (max-width: 768px) {
    /* Navbar */
    .vibe-nav-toggle { display: block}
    .vibe-nav-menu {
        position: fixed;
        left: -100%;
        top: 0;
        height: 100vh;
        width: 280px;
        background: #fff;
        flex-direction: column;
        align-items: flex-start;
        padding: 6rem 2rem;
        gap: 1.5rem;
        transition: left .3s ease;
        box-shadow: 0 10px 15px -3px rgba(0,0,0,.1)}
    .vibe-nav-menu.is-open { left: 0}
    .vibe-nav-menu .dropdown-menu {
        position: static;
        display: block;
        box-shadow: none;
        background: transparent;
        padding: 0.5rem 0 0 1rem;
        border: none;
        min-width: unset;
        margin-top: 0}
    .vibe-nav-menu .dropdown > a { font-weight: bold}
    .vibe-nav-menu .dropdown-menu a {
        padding: 0.5rem 1rem;
        font-weight: 400}

    /* Responsive Logo */
    .logo {
        width: 240px;
        height: 50px}
    .logo_image {
        width: 50px;
        height: 50px}
    .logo_text {
        left: 55px;
        width: 185px;
        height: 30px;
        line-height: 30px;
        font-size: 18px}
    .logo_sub_text {
        left: 55px;
        top: 28px;
        width: 185px;
        height: 20px;
        line-height: 20px;
        font-size: 11px}

    /* Hero */
    .vibe-hero h1 { font-size: 2.25rem}
    .vibe-hero p { font-size: 1.1rem}

    /* Services */
    .service-container, 
    .service-container.reverse {
        flex-direction: column;
        text-align: center;
        gap: 2rem}
}

window.showPage = function(pageId) {
    document.getElementById('home-content').style.display = 'none';
    document.getElementById('full-blog-page').style.display = 'none';
    document.getElementById('organ-donation-page').style.display = 'none';
    document.getElementById('estate-planning-page').style.display = 'none';
    document.getElementById('advance-care-planning-page').style.display = 'none';
    document.getElementById('terms-and-conditions-page').style.display = 'none';
    document.getElementById('privacy-policy-page').style.display = 'none';

    const page = document.getElementById(pageId);
    if (page) {
        page.style.display = 'block';
        document.querySelector('.vibe-nav-menu')?.classList.remove('is-open');
        document.querySelector('.vibe-nav-toggle')?.setAttribute('aria-expanded', 'false');
        window.scrollTo(0, 0);
    }
}

function checkLoginStatus() {
    if (localStorage.getItem('isLoggedIn') === 'true') {
        document.body.classList.add('logged-in');
    } else {
        document.body.classList.remove('logged-in');
    }
}

function renderAllBlogContent() {
    const homepageGrid = document.getElementById('blogGrid');
    const fullPageGrid = document.getElementById('fullBlogGrid');
    if (!homepageGrid || !fullPageGrid) return;

    homepageGrid.innerHTML = '';
    fullPageGrid.innerHTML = '';
    document.querySelectorAll('.blog-post-modal').forEach(modal => modal.remove());

    const posts = JSON.parse(localStorage.getItem('blogPosts')) || [initialPost];

    posts.forEach((post, index) => {
        const escapedTitle = escapeHTML(post.title);
        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = post.content;
        const summary = tempDiv.textContent || tempDiv.innerText || "";
        const shortSummary = escapeHTML(summary.length > 150 ? summary.substring(0, 150) + '...' : summary);
        const altText = post.alt ? escapeHTML(post.alt) : escapedTitle;

        const postCardHTML = `
        <article class=\"vibe-card\">
          ${post.imageUrl ? `<img class=\"vibe-card-media\" src=\"${"" + escapeHTML(post.imageUrl)}\" alt=\"${"" + altText}\">` : ''}
          <div class=\"vibe-card-body\">
            <h4>${escapedTitle}</h4>
            <p>${shortSummary}</p>
            <button class=\"vibe-card-btn\" onclick=\"openModal('${post.id}')\">Read More</button>
          </div>
        </article>`;

        const postModalHTML = `
        <div class=\"vibe-modal blog-post-modal\" id=\"${"" + post.id}\" role=\"dialog\" aria-modal=\"true\" aria-labelledby=\"${"" + post.id}-title\" aria-hidden=\"true\">
          <div class=\"vibe-modal-backdrop\" onclick=\"closeModal('${post.id}')\"></div>
          <div class=\"vibe-modal-panel\" role=\"document\">
            <button class=\"vibe-modal-close\" aria-label=\"Close\" onclick=\"closeModal('${post.id}')\">✕</button>
            ${post.imageUrl ? `<img class=\"vibe-card-media\" style=\"height: 250px; border-radius: 8px; margin-bottom: 1.5rem;\" src=\"${"" + escapeHTML(post.imageUrl)}\" alt=\"${"" + altText}\">` : ''}
            <h3 id=\"${"" + post.id}-title\" style=\"margin-top:0; font-size: 1.75rem;\">${escapedTitle}</h3>
            <div>${post.content}</div>
          </div>
        </div>`;

        fullPageGrid.insertAdjacentHTML('beforeend', postCardHTML);

        if (index < 3) {
            homepageGrid.insertAdjacentHTML('beforeend', postCardHTML);
        }

        document.body.insertAdjacentHTML('beforeend', postModalHTML);
    });
}

function initialize() {
    if (!localStorage.getItem('blogPosts')) {
        localStorage.setItem('blogPosts', JSON.stringify([initialPost]));
    }
    checkLoginStatus();
    renderAllBlogContent();
}

window.clearField = function(arg,val) {
    var field = document.getElementById(arg);
    if(field && field.value == val) {
        field.value = "";
    }
}

window.restoreField = function(arg,val) {
    var field = document.getElementById(arg);
    if(field && field.value == "") {
        field.value = val;
    }
}

document.addEventListener('keydown',e=>{if(e.key==='Escape')document.querySelectorAll('.vibe-modal.is-open').forEach(m=>closeModal(m.id));});
document.querySelector('.vibe-nav-toggle')?.addEventListener('click',e=>{const menu=document.querySelector('.vibe-nav-menu');const open=menu.classList.toggle('is-open');e.currentTarget.setAttribute('aria-expanded',String(open));});
document.querySelectorAll('.vibe-textarea').forEach(t=>{const resize=()=>{t.style.height='auto';t.style.height=(t.scrollHeight)+'px';};t.addEventListener('input',resize);resize();});

const loginForm = document.getElementById('loginForm');
const logoutBtn = document.getElementById('logoutBtn');
const createPostForm = document.getElementById('createPostForm');

const initialPost = {
    id: 'blogPost1Modal',
    title: 'The Gift of Clarity: Why Advance Care Planning Matters',
    imageUrl: 'https://picsum.photos/seed/health/600/300',
    content: `<p>Planning for your future healthcare needs is one of the most significant gifts you can give to your loved ones. It’s a process of thinking about and sharing your preferences for future medical treatment in the event you are unable to communicate them yourself. This process is known as Advance Care Planning (ACP).</p>\n              <p>ACP isn't just about filling out forms; it's about having meaningful conversations with the people who matter most. It ensures that your voice is heard and your values are respected, even when you can't speak for yourself. This clarity can lift a heavy burden from your family, who would otherwise be left to make difficult decisions without knowing your wishes.</p>\n              <p>At Angels Outreach, we guide you through this process with compassion and expertise. We provide resources to help you understand your options, document your choices in an advance directive, and communicate them effectively with your family and healthcare providers. Taking these steps today provides peace of mind for tomorrow, for both you and your loved ones.</p>`,
    alt: 'A person considering their healthcare options'
};

loginForm?.addEventListener('submit', (e) => {
    e.preventDefault();
    localStorage.setItem('isLoggedIn', 'true');
    checkLoginStatus();
    closeModal('loginModal');
});

logoutBtn?.addEventListener('click', () => {
    localStorage.removeItem('isLoggedIn');
    checkLoginStatus();
});

createPostForm?.addEventListener('submit', (e) => {
    e.preventDefault();
    const posts = JSON.parse(localStorage.getItem('blogPosts')) || [];
    const newPost = {
        id: 'blogPost' + Date.now(),
        title: document.getElementById('postTitle').value,
        imageUrl: document.getElementById('postImageUrl').value,
        alt: document.getElementById('postImageAlt').value,
        content: document.getElementById('postContentEditor').innerHTML,
    };
    posts.unshift(newPost);
    localStorage.setItem('blogPosts', JSON.stringify(posts));

    renderAllBlogContent();
    closeModal('createPostModal');
    createPostForm.reset();
